import { Component } from '@angular/core';
import { PaymentService } from '../services/payment.service';

@Component({
  selector: 'app-payment',
  templateUrl: './payment.component.html',
  styleUrls: ['./payment.component.css']
})
export class PaymentComponent {
  paymentData = {
    amount: 0,
    currency: '',
    itemName: '',
    itemPrice: 0,
    itemQuantity: 0,
    customerName: '',
    customerEmail: '',
    customerPhone: '',
  };

  constructor(private paymentService: PaymentService) { }

  submitPayment() {
    const orderRequest = {
      amount: this.paymentData.amount,
      currency: this.paymentData.currency,
      lineItems: [{
        name: this.paymentData.itemName,
        unitPrice: this.paymentData.itemPrice,
        quantity: this.paymentData.itemQuantity
      }],
      customerInfo: {
        name: this.paymentData.customerName,
        email: this.paymentData.customerEmail,
        phone: this.paymentData.customerPhone
      }
    };

    this.paymentService.createPaymentLink(orderRequest).subscribe({
      next: (response) => {
        console.log('Link de pago creado:', response);
        // Aquí puedes manejar la respuesta, como mostrar un mensaje de éxito
      },
      error: (error) => {
        console.error('Error al crear el link de pago:', error);
        // Manejar el error, posiblemente mostrando un mensaje al usuario
      }
    });
  }
}
<form (ngSubmit)="submitPayment()">
    <div>
        <label for="amount">Monto:</label>
        <input type="number" id="amount" [(ngModel)]="paymentData.amount"
            name="amount" required>
    </div>
    <div>
        <label for="currency">Moneda:</label>
        <select id="currency" [(ngModel)]="paymentData.currency" name="currency"
            required>
            <option value="MXN">MXN</option>
            <option value="USD">USD</option>
        </select>
    </div>
    <div>
        <label for="itemName">Nombre del Artículo:</label>
        <input type="text" id="itemName" [(ngModel)]="paymentData.itemName"
            name="itemName" required>
    </div>
    <div>
        <label for="itemPrice">Precio del Artículo:</label>
        <input type="number" id="itemPrice" [(ngModel)]="paymentData.itemPrice"
            name="itemPrice" required>
    </div>
    <div>
        <label for="itemQuantity">Cantidad:</label>
        <input type="number" id="itemQuantity"
            [(ngModel)]="paymentData.itemQuantity" name="itemQuantity" required>
    </div>
    <div>
        <label for="customerName">Nombre del Cliente:</label>
        <input type="text" id="customerName"
            [(ngModel)]="paymentData.customerName" name="customerName" required>
    </div>
    <div>
        <label for="customerEmail">Email del Cliente:</label>
        <input type="email" id="customerEmail"
            [(ngModel)]="paymentData.customerEmail" name="customerEmail"
            required>
    </div>
    <div>
        <label for="customerPhone">Teléfono del Cliente:</label>
        <input type="tel" id="customerPhone"
            [(ngModel)]="paymentData.customerPhone" name="customerPhone"
            required>
    </div>

    <button type="submit">Enviar Pago</button>
</form>
